import React from "react";
import "./Table.css";

function Table(props) {
  const tableRows = props.tableData.map((rowData) => {
    const rowKey = rowData[0]; // use first element as unique key
    return (
      <tr key={rowKey}>
        {rowData.map((cellData, cellIndex) => {
          const cellKey = `${rowKey}-${cellIndex}`; // use rowKey and cellIndex as unique key
          return (
            <td
              key={cellKey}
              onDrop={(event) => props.onDrop(event, rowKey, cellIndex)}
              onDragOver={props.onDragOver}
            >
              {cellData}
            </td>
          );
        })}
      </tr>
    );
  });

  return (
    <div>
      <table style={{ width: 1376, border: "solid #E6E6E6", backgroundColor: "#F8F8F8" }}>
        <thead>{tableRows[0]}</thead>
        <tbody>{tableRows.slice(1)}</tbody>
      </table>
    </div>
  );
}

export default Table;
