import React from "react";
import "./Table.css";

function Table(props) {

 const tableRows = props.tableData.map((rowData, index) => (
  <tr key={index}>
    {rowData.map((cellData, cellIndex) => {
      if (Array.isArray(cellData)) {
        return (
          <td key={`${index}-${cellIndex}`}>
            {cellData.map((buttonData, buttonIndex) => (
              <button
                key={`${index}-${cellIndex}-${buttonIndex}`}
                onDragStart={(event) => {
                  event.dataTransfer.setData("button", buttonData);
                }}
                draggable
              >
                {buttonData}
              </button>
            ))}
          </td>
        );
      } else {
        return (
          <td
            key={`${index}-${cellIndex}`}
            onDrop={props.onDrop}
            onDragOver={props.onDragOver}
          >
            {cellData}
          </td>
        );
      }
    })}
  </tr>
));
}

export default Table;
