import React, { useState } from "react";
import Table from "./Components/Table";
import Mapping from "./Components/Mapping";
function App() {
  const [buttonText, setButtonText] = useState("");
const [tabledata, settabledata] = useState([
  ["", "Autumn 1", "Autumn 2 ", "Autumn 3", "Autumn 4", "Autumn 5", "Autumn 6"],
  ["EYFS", "", "", "", "", "", ""],
  ["KSY1", "", "", "", "", "", ""],
  ["KSY2", "", "", "", "", "", ""],
  ["KSY3", "", "", "", "", "", ""]]
);


const handleDrop = (event, rowIndex, cellIndex) => {
  event.preventDefault();
  const data = event.dataTransfer.getData("button");
  const target = event.target;
  const existingContent = target.innerHTML;
  const buttonCount = (existingContent.match(/class="button"/g) || []).length;
  if (buttonCount < 2) {
    target.innerHTML = existingContent + data;
    const newData = [...tabledata];
    newData[rowIndex][cellIndex] = target.innerHTML;
    settabledata(newData);
  }
};

  const handleDragOver = (event) => {
    event.preventDefault();
  };

  return (
    <div style={{display:"flex",alignItems:"center",flexDirection:"column",justifyContent:"center"}}>
     <div style={{marginTop:40}}> <Mapping /></div>
     <div style={{marginTop:24}}>
      <Table buttonText={buttonText} onDrop={handleDrop} onDragOver={handleDragOver} tableData={tabledata}/>
      </div>
    </div>
  );
}


export default App;
