import React, { useState } from "react";
import './Table.css'

const Buttons = (props) => {
  const [dragging, setDragging] = useState(false);
  const [droppedButtons, setDroppedButtons] = useState([]);

  const handleDragStart = (event) => {
    const button = event.currentTarget;
    event.dataTransfer.setDragImage(button, 0, 0);
    event.dataTransfer.setData("button", button.outerHTML);
  };

  const handleDragEnd = () => {
    setDragging(false);
  };

  const handleDelete = (event) => {
    event.preventDefault();
    const buttonData = event.target.dataset.button;
    const newDroppedButtons = droppedButtons.filter(
      (droppedButton) => droppedButton !== buttonData
    );
    setDroppedButtons(newDroppedButtons);

    const newData = [...tabledata];
    for (let i = 1; i < newData.length; i++) {
      for (let j = 1; j < newData[i].length; j++) {
        if (newData[i][j].includes(buttonData)) {
          newData[i][j] = newData[i][j].replace(buttonData, "");
        }
      }
    }
    settabledata(newData);
  };
  return (
    <div>
      <button
  className="button"
  onClick={() => window.location.href = 'https://facebook.com'}
  draggable="true"
  onDragStart={handleDragStart}
  onDragEnd={handleDragEnd} 
>
  {props.button}
</button>

    </div>
  );
};


export default Buttons;
