import React from "react";
import "./Table.css";

function Table(props) {
  const tableRows = props.tableData.map((rowData, rowIndex) => (
    <tr key={rowIndex}>
      {rowData.map((cellData, cellIndex) => {
        return (
          <td
            key={`${rowIndex}-${cellIndex}`}
            id={`cell-${rowIndex}-${cellIndex}`}
            onDrop={(event) => props.onDrop(event, rowIndex, cellIndex)}
            onDragOver={props.onDragOver}
          >
            <div dangerouslySetInnerHTML={{ __html: cellData }} />
          </td>
        );
      })}
    </tr>
  ));

  return (
    <div>
      <table style={{ width: 1376, border: "solid #E6E6E6", backgroundColor: "#F8F8F8" }}>
        <thead>{tableRows[0]}</thead>
        <tbody>{tableRows.slice(1)}</tbody>
      </table>
    </div>
  );
}

export default Table;
