import React, { useState } from "react";
import './Table.css'

const Buttons = (props) => {
  const [dragging, setDragging] = useState(false);
  const [deleteMarked, setDeleteMarked] = useState(false);

  const handleDragStart = (event) => {
    const button = event.currentTarget;
    event.dataTransfer.setDragImage(button, 0, 0);
    event.dataTransfer.setData("button", button.outerHTML);
  };

  const handleDragEnd = () => {
    setDragging(false);
  };

  const handleDelete = () => {
    setDeleteMarked(true);
    props.onDelete(props.button);
  }

  const buttonStyle = {
    textDecoration: deleteMarked ? 'line-through' : 'none'
  };

  return (
    <div>
      <button
        className="button"
        onClick={() => window.location.href = 'https://facebook.com'}
        draggable="true"
        onDragStart={handleDragStart}
        onDragEnd={handleDragEnd} 
        style={buttonStyle}
      >
        {props.button}
      </button>
      {deleteMarked &&
        <button onClick={() => setDeleteMarked(false)}>Undo Delete</button>
      }
    </div>
  );
};


export default Buttons;
