import React, { useState } from "react";
import './Table.css'

const Buttons = (props) => {
  const [dragging, setDragging] = useState(false);
  const [buttonText, setButtonText] = useState(props.button);

  const handleDragStart = (event) => {
    setDragging(true);
    event.dataTransfer.setData("text/plain", buttonText);  
    event.dataTransfer.setDragImage(event.target, 0, 0); // set the button as the drag image
    event.dataTransfer.setData("button", event.target); // set the button element as data to be transferred
  };

  const handleDragEnd = () => {
    setDragging(false);
  };

  const handleDrop = (event) => {
    event.preventDefault();
    const data = event.dataTransfer.getData("text/plain");
    event.target.textContent = data;
  };

  const handleDragOver = (event) => {
    event.preventDefault();
  };

  return (
    <div>
      <button className="button"
        draggable="true"
        onDragStart={handleDragStart}
        onDragEnd={handleDragEnd} 
      >
        {props.button}
      </button>
    </div>
  );
};

export default Buttons;
