import React from "react";
import "./Table.css";

function Table(props) {
  console.log(props.buttonText);

  const tableData = [
    ["", "Autumn1", "Autumn2", "Autumn3", "Autumn4", "Autumn5", "Autumn6"],
    ["", "", "", "", "", ""],
    ["KSY1", "", "", "", "", "", ""],
    ["KSY2", "", "", "", "", "", ""],
    ["KSY3", "", "", "", "", "", ""]
  ];

  return (
    <div>
      <table>
        <thead>
          <tr>
            {tableData[0].map((col, index) => (
              <th key={index}>{col}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {tableData.slice(1).map((row, rowIndex) => (
            <tr key={rowIndex}>
              {row.map((col, colIndex) => (
                <td
                  key={colIndex}
                  onDrop={props.onDrop}
                  onDragOver={props.onDragOver}
                >
                  {col === "EYFS" && props.buttonText && <button>{props.buttonText}</button>}
                  {col !== "EYFS" && col}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Table;
