function Table(props) {
  const handleDelete = (event, rowIndex, cellIndex) => {
    const updatedTableData = [...props.tableData];
    updatedTableData[rowIndex][cellIndex] = "";
    props.setTableData(updatedTableData);
  };

  const tableRows = props.tableData.map((rowData, rowIndex) => (
    <tr key={rowIndex}>
      {rowData.map((cellData, cellIndex) => (
        <td
          key={`${rowIndex}-${cellIndex}`}
          onDrop={props.onDrop}
          onDragOver={props.onDragOver}
        >
          {cellData}
          {cellData.includes("<button") && (
            <div
              className="delete-container"
              onDrop={(event) => {
                event.preventDefault();
                handleDelete(event, rowIndex, cellIndex);
              }}
              onDragOver={(event) => {
                event.preventDefault();
              }}
            >
              Drop here to delete
            </div>
          )}
        </td>
      ))}
    </tr>
  ));

  return (
    <div>
      <table
        style={{
          width: 1376,
          border: "solid #E6E6E6",
          backgroundColor: "#F8F8F8",
        }}
      >
        <thead>{tableRows[0]}</thead>
        <tbody>{tableRows.slice(1)}</tbody>
      </table>
    </div>
  );
}
