import React from "react";
import "./Table.css";

function Table(props) {
  const newTableData = [...tabledata]; // create a copy of the original array
  newTableData[1][2] = "New Value"; // set the value in the desired index
  settabledata(newTableData); // update the state with the new array

  const tableRows = props.tableData.map((rowData, index) => (
    <tr key={index}>
      {rowData.map((cellData, cellIndex) =>{

      return(

        <td
          key={`${index}-${cellIndex}`}
          onDrop={props.onDrop}
          onDragOver={props.onDragOver}
        >
          {cellData}

        </td>
      )})}
    </tr>
  ));

  return (
    <div>
      <table style={{width:1376,border: "solid #E6E6E6",backgroundColor:"#F8F8F8"}}>
        <thead>
          {tableRows[0]}
        </thead>
        <tbody>
          {tableRows.slice(1)}
        </tbody>
      </table>
    </div>
  );
}

export default Table;
