import React from "react";
import "./Table.css";

function Table(props) {

  const tableRows = props.tableData.map((rowData, index) => (
    <tr key={index}>
      {rowData.map((cellData, cellIndex) => {
        return (
          <td
            key={`${index}-${cellIndex}`}
            onDrop={props.onDrop}
            onDragOver={props.onDragOver}
            dangerouslySetInnerHTML={{
              __html: cellData
                .replace(/×<\/span>/g, `×</span><script>document.currentScript.parentNode.querySelector('.delete-button').onclick = function() { window.React_handleButtonDelete(${index}, ${cellIndex}, this.parentNode.outerHTML); }</script>`)
            }}
          />
        );
      })}
    </tr>
  ));

  window.React_handleButtonDelete = (rowIndex, cellIndex, buttonContent) => {
    props.handleButtonDelete(rowIndex, cellIndex, buttonContent);
  };


  return (
    <div>
      <table style={{width:1376,border: "solid #E6E6E6",backgroundColor:"#F8F8F8"}}>
        <thead>
          {tableRows[0]}
        </thead>
        <tbody>
          {tableRows.slice(1)}
        </tbody>
      </table>
    </div>
  );
}

export default Table;
