import React, { useState } from "react";
import Table from "./Components/Table";
import Mapping from "./Components/Mapping";
import Buttons from "./Components/Buttons";

function App() {
  const [dragging, setDragging] = useState(false);
  const [button, setButton] = useState(null);

  const handleDragStart = (event, button) => {
    setDragging(true);
    setButton(button);
    if (button) {
      event.dataTransfer.setData("application/button", JSON.stringify(button));
    }
  };

  const handleDragEnd = () => {
    setDragging(false);
    setButton(null);
  };

  const handleDrop = (event) => {
    event.preventDefault();
    const data = event.dataTransfer.getData("application/button");
    console.log("data:", data); // add a console log to see the value of data
    if (data) {
      try {
        const parsedData = JSON.parse(data);
        console.log("parsedData:", parsedData); // add a console log to see the parsed data
        setButton(null);
        event.target.appendChild(parsedData);
      } catch (e) {
        console.error(e); // add an error log if there is an issue parsing the data
      }
    }
  };


  const handleDragOver = (event) => {
    event.preventDefault();
  };

  return (
    <div>
      <Mapping onDragStart={handleDragStart} onDragEnd={handleDragEnd} />
      <Table onDrop={handleDrop} onDragOver={handleDragOver} />
    </div>
  );
}

export default App;
