import React, { useState } from "react";
import "./Table.css";

const Buttons = (props) => {
  const [dragging, setDragging] = useState(false);
  const [visible, setVisible] = useState(true);

  const handleDragStart = (event) => {
    const button = event.currentTarget;
    event.dataTransfer.setDragImage(button, 0, 0);
    event.dataTransfer.setData("button", button.outerHTML);
  };

  const handleDragEnd = () => {
    setDragging(false);
  };

  const handleDelete = () => {
    setVisible(false);
    const newDroppedButtons = [...props.droppedButtons];
    const index = newDroppedButtons.indexOf(props.button);
    if (index > -1) {
      newDroppedButtons.splice(index, 1);
      props.setDroppedButtons(newDroppedButtons);
    }
  };

  return (
    <>
      {visible && (
        <div>
          <button
            className="button"
            onClick={() => (window.location.href = "https://facebook.com")}
            draggable="true"
            onDragStart={handleDragStart}
            onDragEnd={handleDragEnd}
          >
            {props.button}
          </button>
          <button onClick={handleDelete}>X</button>
        </div>
      )}
    </>
  );
};

export default Buttons;
