import React from "react";
import "./Table.css";

function Table(props) {

  const tableRows = props.tableData.map((rowData, index) => (
    <tr key={index}>
      {rowData.map((cellData, cellIndex) =>{
        // check if the cellData is an array
        if(Array.isArray(cellData)) {
          // map through the nested array to render the buttons
          return (
            <td
              key={`${index}-${cellIndex}`}
              onDrop={props.onDrop}
              onDragOver={props.onDragOver}
            >
              {cellData.map((buttonText, buttonIndex) => (
                <button key={`${index}-${cellIndex}-${buttonIndex}`} draggable="true" onDragStart={(event) => event.dataTransfer.setData("button", buttonText)}>
                  {buttonText}
                </button>
              ))}
            </td>
          )
        } else {
          return (
            <td
              key={`${index}-${cellIndex}`}
              onDrop={props.onDrop}
              onDragOver={props.onDragOver}
            >
              {cellData}
            </td>
          )
        }
      })}
    </tr>
  ));

  return (
    <div>
      <table style={{width:1376,border: "solid #E6E6E6",backgroundColor:"#F8F8F8"}}>
        <thead>
          {tableRows[0]}
        </thead>
        <tbody>
          {tableRows.slice(1)}
        </tbody>
      </table>
    </div>
  );
}

export default Table;
