const Mapping = (props) => {
  const drag = [
    {
      id: 1,
      button: "GYMNASTICS",
    },
    {
      id: 2,
      button: "ATHLETICS",
    },
    {
      id: 3,
      button: "BASKETBALL",
    },
    {
      id: 4,
      button: "CRICKET",
    },
    {
      id: 5,
      button: "DANCE",
    },
    {
      id: 6,
      button: "FITNESS",
    },
    {
      id: 7,
      button: "RUGBY",
    },
    {
      id: 8,
      button: "8",
    },
  ];

  return (
    <>
      <div
        style={{
          width: 1376,
          height: 242,
          borderRadius: 2,
          border: "solid red",
        }}
      >
        <h1
          style={{
            fontWeight: 700,
            fontSize: 22,
            textAlign: "initial",
            marginTop: 25,
            marginLeft: 24,
            display: "block",
          }}
        >
          Activities
        </h1>
        <div className="drag" style={{ display: "flex" }}>
          {drag.map((buttontxt) => {
            return (
              <Buttons
                key={buttontxt.id}
                id={buttontxt.id}
                button={buttontxt.button}
                handleDragStart={props.handleDragStart}
                handleDragEnd={props.handleDragEnd}
              />
            );
          })}
        </div>
      </div>
    </>
  );
};
