import React from "react";
import "./Table.css";

function Table(props) {

  const tableRows = tabledata.map((rowData, rowIndex) => (
    <tr key={rowIndex}>
      {rowData.map((cellData, cellIndex) => (
       <td
       key={`${index}-${cellIndex}`}
       id={`cell-${index}-${cellIndex}`}
       onDrop={(event) => props.onDrop(event, index, cellIndex)}
       onDragOver={props.onDragOver}
     >
       {cellData}
     </td>

      ))}
    </tr>
  ));

  return (
    <div>
      <table style={{width:1376,border: "solid #E6E6E6",backgroundColor:"#F8F8F8"}}>
        <thead>
          {tableRows[0]}
        </thead>
        <tbody>
          {tableRows.slice(1)}
        </tbody>
      </table>
    </div>
  );
}

export default Table;
