import React, { useState } from "react";
import Table from "./Components/Table";

function App() {
  const [dragging, setDragging] = useState(false);
  const [buttonText, setButtonText] = useState("Drag and Drop Button");

  const handleDragStart = (event) => {
    setDragging(true);
    event.dataTransfer.setData("text/plain", buttonText);
    event.dataTransfer.setDragImage(event.target, 0, 0);
    event.dataTransfer.setData("button", event.target);
  };

  const handleDragEnd = () => {
    setDragging(false);
  };

  const handleDrop = (event) => {
    event.preventDefault();
    const data = event.dataTransfer.getData("button");
    event.target.appendChild(data);
  };

  const handleDragOver = (event) => {
    event.preventDefault();
  };

  return (
    <div>
      <button
        draggable="true"
        onDragStart={handleDragStart}
        onDragEnd={handleDragEnd}
        style={{ backgroundColor: dragging ? "red" : "blue" }}
      >
        {buttonText}
      </button>
      <Table onDrop={handleDrop} onDragOver={handleDragOver} />
      <table>
        <tbody>
          <tr>
            <td onDrop={handleDrop} onDragOver={handleDragOver}></td>
            <td onDrop={handleDrop} onDragOver={handleDragOver}></td>
            <td onDrop={handleDrop} onDragOver={handleDragOver}></td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

export default App;
