import React, { useState } from "react";

function Deletebtn(props) {
  const [droppedButtons, setDroppedButtons] = useState([]);

  const handleDrop = (event) => {
    event.preventDefault();
    const data = event.dataTransfer.getData("button");

    // Check if the dropped data is a button
    if (!data.includes("<button")) {
      return;
    }

    // Remove the dropped button from the droppedButtons state array
    const index = droppedButtons.indexOf(data);
    if (index > -1) {
      const newDroppedButtons = [...droppedButtons];
      newDroppedButtons.splice(index, 1);
      setDroppedButtons(newDroppedButtons);
    }
  };

  const handleDragOver = (event) => {
    event.preventDefault();
  };

  return (
    <div
      style={{
        width: 100,
        height: 100,
        backgroundColor: "red",
      }}
      onDrop={handleDrop}
      onDragOver={handleDragOver}
    >
      Drop here to delete
      {droppedButtons.map((button, index) => (
        <div
          key={index}
          dangerouslySetInnerHTML={{
            __html: button,
          }}
        />
      ))}
    </div>
  );
}

export default Deletebtn;
