import React, { useState } from "react";
import Table from "./Components/Table";
import Mapping from "./Components/Mapping";

function App() {
  const [dragging, setDragging] = useState(false);
  const [buttonText, setButtonText] = useState(null);

  const handleDragStart = (event,buttonText) => {
    setDragging(true);
    setButtonText(buttonText)
    if (buttonText) {
      event.dataTransfer.setData("application/button", JSON.stringify(buttonText));
    }
  };

  const handleDragEnd = () => {
    setDragging(false);
    setButtonText(null);
  };

  const handleDrop = (event) => {
    event.preventDefault();
    const data = JSON.parse(event.dataTransfer.getData("application/button"));
    if (data) {
      setButtonText(null);
      event.target.appendChild(data);
    }
  };
  const handleDragOver = (event) => {
    event.preventDefault();
  };


  return (
    <div>
      {/* <button
        draggable="true"
        onDragStart={handleDragStart}
        onDragEnd={handleDragEnd}
        style={{ backgroundColor: dragging ? "red" : "blue" }}
      >
        {buttonText}
      </button> */}
      <Mapping />
      <Table buttonText={buttonText} onDrop={handleDrop} onDragOver={handleDragOver} />
      {/* <table>
        <tbody>
          <tr>
            <td onDrop={handleDrop} onDragOver={handleDragOver}></td>
            <td onDrop={handleDrop} onDragOver={handleDragOver}></td>
            <td onDrop={handleDrop} onDragOver={handleDragOver}></td>
          </tr>
        </tbody>
      </table> */}
    </div>
  );
}

export default App;
