import React from "react";
import "./Table.css";

function Table(props) {
  console.log(props.buttonText);
  console.log(props.onDrop);

  // create an array to hold the table data
  const tableData = [
    [{ id: "th1" }, { id: "th2" }, { id: "th3" }, { id: "th4" }, { id: "th5" }, { id: "th6" }],
    [{ id: "td1-1" }, { id: "td1-2" }, { id: "td1-3" }, { id: "td1-4" }, { id: "td1-5" }, { id: "td1-6" }],
    [{ id: "td2-1" }, { id: "td2-2" }, { id: "td2-3" }, { id: "td2-4" }, { id: "td2-5" }, { id: "td2-6" }],
    [{ id: "td3-1" }, { id: "td3-2" }, { id: "td3-3" }, { id: "td3-4" }, { id: "td3-5" }, { id: "td3-6" }],
    [{ id: "td4-1" }, { id: "td4-2" }, { id: "td4-3" }, { id: "td4-4" }, { id: "td4-5" }, { id: "td4-6" }],
  ];

  // loop through the table data and render the table elements
  const tableRows = tableData.map((row, rowIndex) => (
    <tr key={`row-${rowIndex}`}>
      {row.map((cell, cellIndex) => (
        <td key={`cell-${rowIndex}-${cellIndex}`} id={cell.id} onDrop={props.onDrop} onDragOver={props.onDragOver}>
          <button></button>
        </td>
      ))}
    </tr>
  ));

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th id="th0"></th>
            <th id="th1">Autumn1</th>
            <th id="th2">Autumn2</th>
            <th id="th3">Autumn3</th>
            <th id="th4">Autumn4</th>
            <th id="th5">Autumn5</th>
            <th id="th6">Autumn6</th>
          </tr>
        </thead>
        <tbody>{tableRows}</tbody>
      </table>
    </div>
  );
}

export default Table;
