import React from "react";
import "./Table.css";

function Buttons(props) {
  const handleDragStart = (event) => {
    event.dataTransfer.setData("button", event.target.innerText);
  };
  return (
    <div
      draggable // add this attribute
      onDragStart={handleDragStart}
      style={{
        backgroundColor: "#FFFFFF",
        borderRadius: "4px",
        border: "solid #E6E6E6",
        color: "#282828",
        fontSize: "14px",
        fontWeight: "500",
        height: "32px",
        lineHeight: "20px",
        marginRight: "8px",
        marginBottom: "8px",
        padding: "6px 12px",
        textAlign: "center",
        width: "auto",
      }}
    >
      {props.button}
    </div>
  );
}


export default Table;
