import React, { useState } from "react";
import Table from "./Components/Table";
import Mapping from "./Components/Mapping";
function App() {
  const [buttonText, setButtonText] = useState("");
const [tabledata, settabledata] = useState([
  ["", "Autumn 1", "Autumn 2 ", "Autumn 3", "Autumn 4", "Autumn 5", "Autumn 6"],
  ["EYFS", "", "", "", "", "", ""],
  ["KSY1", "", "", "", "", "", ""],
  ["KSY2", "", "", "", "", "", ""],
  ["KSY3", "", "", "", "", "", ""]]
);
const updateTableData = (rowIndex, cellIndex, value) => {
  const newData = [...tabledata]; // create a copy of the array
  newData[rowIndex][cellIndex] = value; // modify the value of the desired index
  settabledata(newData); // set the state with the updated array
}

  const handleDrop = (event) => {
    event.preventDefault();
    const data = event.dataTransfer.getData("button");
    event.target.innerHTML = data; 
    // settabledata(data)
    setButtonText(data); // set the buttonText state to the dropped button element
  };


  const handleDragOver = (event) => {
    event.preventDefault();
  };

  return (
    <div style={{display:"flex",alignItems:"center",flexDirection:"column",justifyContent:"center"}}>
     <div style={{marginTop:40}}> <Mapping /></div>
     <div style={{marginTop:24}}>
      <Table buttonText={buttonText} onDrop={handleDrop} onDragOver={handleDragOver} tableData={tabledata}/>
      </div>
    </div>
  );
}


export default App;
