  {
    id: 1,
    button: "1",
  },
  {
    id: 2,
    button: "2",
  },
  {
    id: 3,
    button: "3",
  },
  {
    id: 3,
    button: "4",
  },
  {
    id: 4,
    button: "5",
  },
  {
    id: 5,
    button: "6",
  },
  {
    id: 6,
    button: "7",
  },
  {
    id: 7,
    button: "8",
  },
];
export default function Mapping() {
  return (
    <DndProvider backend={HTML5Backend}>
      <div
        style={{
          width: 1376,
          height: 242,
          borderRadius: 2,
          border: "solid red",
        }}
      >
        <h1
          style={{
            fontWeight: 700,
            fontSize: 22,
            textAlign: "initial",
            marginTop: 25,
            marginLeft: 24,
            display: "block",
          }}
        >
          Activites
        </h1>
        <div className="drag">
          {drag.map((buttontxt) => {
            return "hi";
          })}
        </div>
      </div>
    </DndProvider>
  );
}
