import React from "react";
import "./Table.css";

function Table(props) {
  console.log(props.buttonText);
  console.log(props.onDrop);

  // Define the table headers and cell values
  const headers = ["", "Autumn1", "Autumn2", "Autumn3", "Autumn4", "Autumn5", "Autumn6"];
  const rows = [
    ["EYFS", "", "", "", "", "", ""],
    ["KSY1", "", "", "", "", "", ""],
    ["KSY2", "", "", "", "", "", ""],
    ["KSY3", "", "", "", "", "", ""],
  ];

  // Generate a unique ID for each table element
  let idCount = 0;
  const getId = () => `table-item-${idCount++}`;

  // Map over the headers and rows to create the 2D array of table elements
  const tableData = [headers, ...rows].map((row) =>
    row.map((cell) => ({
      id: getId(),
      value: cell,
    }))
  );

  return (
    <div>
      <table>
        <thead>
          {tableData[0].map((cell) => (
            <th key={cell.id} id={cell.id}>
              {cell.value}
            </th>
          ))}
        </thead>
        <tbody>
          {tableData.slice(1).map((row) => (
            <tr key={row[0].id} id={row[0].id}>
              {row.map((cell) => (
                <td key={cell.id} id={cell.id} onDrop={props.onDrop} onDragOver={props.onDragOver}>
                  <button></button>
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Table;
