import React from "react";
import "./Table.css";

function Table(props) {

  const handleDelete = (event, rowIndex, cellIndex) => {
    event.stopPropagation();
    const newData = [...props.tableData];
    newData[rowIndex][cellIndex] = "";
    props.settabledata(newData);
  };

  const tableRows = props.tableData.map((rowData, rowIndex) => (
    <tr key={rowIndex}>
      {rowData.map((cellData, cellIndex) => (
        <td
          key={`${rowIndex}-${cellIndex}`}
          onDrop={props.onDrop}
          onDragOver={props.onDragOver}
        >
          <div
            className="button-container"
            dangerouslySetInnerHTML={{ __html: cellData }}
          >
            {cellData && (
              <button
                className="delete-button"
                onClick={(event) => handleDelete(event, rowIndex, cellIndex)}
              >
                X
              </button>
            )}
          </div>
        </td>
      ))}
    </tr>
  ));


  return (
    <div>
      <table style={{width:1376,border: "solid #E6E6E6",backgroundColor:"#F8F8F8"}}>
        <thead>
          {tableRows[0]}
        </thead>
        <tbody>
          {tableRows.slice(1)}
        </tbody>
      </table>
    </div>
  );
}

export default Table;
