import React, { useState } from "react";
import Table from "./Components/Table";
import Mapping from "./Components/Mapping";
function App() {
  const [dragging, setDragging] = useState(false);
  const [buttonText, setButtonText] = useState("");

  const handleDragStart = (event) => {
    setDragging(true);
    setButtonText(event.target.textContent); // set the buttonText state to the button text
    event.dataTransfer.setData("text/plain", buttonText);
  };

  const handleDragEnd = () => {
    setDragging(false);
  };

  const handleDrop = (event) => {
    event.preventDefault();
    const data = event.dataTransfer.getData("text/plain");
    event.target.textContent = data;
    setButtonText(data); // set the buttonText state to the dropped text
  };

  const handleDragOver = (event) => {
    event.preventDefault();
  };

  return (
    <div>
      <Mapping />
      <Table buttonText={buttonText} onDrop={handleDrop} onDragOver={handleDragOver} />
    </div>
  );
}


export default App;
