import React from "react";
import "./Table.css";
import Buttons from "./Buttons";

function Table(props) {
  const handleDelete = (button) => {
    const newData = props.tableData.map((rowData) =>
      rowData.map((cellData) => cellData.replace(button, ""))
    );
    props.settabledata(newData);
  };

  const tableRows = props.tableData.map((rowData, index) => (
    <tr key={index}>
      {rowData.map((cellData, cellIndex) => {
        const hasButton = cellData.includes(props.buttonText);
        const showDelete = hasButton && cellData.trim().length > 0;
        return (
          <td
            key={`${index}-${cellIndex}`}
            onDrop={props.onDrop}
            onDragOver={props.onDragOver}
          >
            <div className="cell-wrapper">
              {hasButton && (
                <Buttons
                  button={props.buttonText}
                  onDelete={handleDelete}
                  showDelete={showDelete}
                />
              )}
            </div>
          </td>
        );
      })}
    </tr>
  ));

  return (
    <div>
      <table
        style={{
          width: 1376,
          border: "solid #E6E6E6",
          backgroundColor: "#F8F8F8",
        }}
      >
        <thead>{tableRows[0]}</thead>
        <tbody>{tableRows.slice(1)}</tbody>
      </table>
    </div>
  );
}

export default Table;
