import React from "react";
import "./Table.css";
function Table(props) {
  console.log(props.buttonText);

  // Create a 2D array to store the table data
  const tableData = [
    ["", "Autumn1", "Autumn2", "Autumn3", "Autumn4", "Autumn5", "Autumn6"],
    ["EYFS", "", "", "", "", "", ""],
    ["KSY1", "", "", "", "", "", ""],
    ["KSY2", "", "", "", "", "", ""],
    ["KSY3", "", "", "", "", "", ""],
  ];

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th></th>
            <th>Autumn1</th>
            <th>Autumn2</th>
            <th>Autumn3</th>
            <th>Autumn4</th>
            <th>Autumn5</th>
            <th>Autumn6</th>
          </tr>
        </thead>
        <tbody>
          {tableData.map((row, rowIndex) => (
            <tr key={rowIndex}>
              {row.map((cell, cellIndex) => (
                <td
                  key={cellIndex}
                  onDrop={props.onDrop}
                  onDragOver={props.onDragOver}
                >
                  {cell === props.buttonText && <button>{cell}</button>}
                  {cell !== props.buttonText && cell}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Table;
