import React, { useState } from "react";
import './Table.css'

const Buttons = (props) => {
  const [dragging, setDragging] = useState(false);

  const handleDragStart = (event) => {
    const button = event.currentTarget;
    event.dataTransfer.setDragImage(button, 0, 0);
    event.dataTransfer.setData("button", button.outerHTML);
  };

  const handleDragEnd = () => {
    setDragging(false);
  };

  const handleDelete = () => {
    props.setDroppedButtons((buttons) =>
      buttons.filter((button) => button !== props.button)
    );
  };

  return (
    <div>
      <button
        className="button"
        onClick={() => window.location.href = 'https://facebook.com'}
        draggable="true"
        onDragStart={handleDragStart}
        onDragEnd={handleDragEnd}
      >
        {props.button}
      </button>
      <button onClick={handleDelete}>X</button>
    </div>
  );
};

export default Buttons;
