import React from "react";
import "./Table.css";

function Table(props) {

  const tableRows = tabledata.map((rowData, rowIndex) => (
    <tr key={rowIndex}>
      {rowData.map((cellData, cellIndex) => (
        <td
          key={`${rowIndex}-${cellIndex}`}
          onDrop={(event) => handleDrop(event, rowIndex, cellIndex)}
          onDragOver={(event) => event.preventDefault()}
          dangerouslySetInnerHTML={{ __html: cellData }}
        />
      ))}
    </tr>
  ));

  <Table tableData={tabledata} onDrop={handleDrop.bind(this)} />

  return (
    <div>
      <table style={{width:1376,border: "solid #E6E6E6",backgroundColor:"#F8F8F8"}}>
        <thead>
          {tableRows[0]}
        </thead>
        <tbody>
          {tableRows.slice(1)}
        </tbody>
      </table>
    </div>
  );
}

export default Table;
