import React from 'react'

function Deletebtn({ onDrop }) {
  const handleDrop = (event) => {
    event.preventDefault();
    const data = event.dataTransfer.getData("button");

    // Remove the dropped button from the cell
    const cell = event.target.closest("td");
    const buttonWrapper = cell.querySelector(`[data-button="${data}"]`);
    if (!buttonWrapper) return;
    cell.removeChild(buttonWrapper);

    // Get the updated cell content and update the table data state
    const updatedContent = cell.innerHTML;
    const rowIndex = cell.parentNode.rowIndex;
    const cellIndex = cell.cellIndex;
    const updatedTableData = [...tabledata];
    updatedTableData[rowIndex][cellIndex] = updatedContent;
    settabledata(updatedTableData);

    // Add the dropped button to the droppedButtons state array
    setDroppedButtons((prevState) =>
      prevState.filter((button) => button !== data)
    );
  };

  const handleDragOver = (event) => {
    event.preventDefault();
  };

  return (
    <div
      style={{ width: "100px", height: "100px", backgroundColor: "red" }}
      onDrop={handleDrop}
      onDragOver={handleDragOver}
    >
      Drop here to delete
    </div>
  );
}

export default Deletebtn;
