import React from "react";

function Deletebtn(props) {
  const handleDrop = (event) => {
    event.preventDefault();
    const data = event.dataTransfer.getData("button");
    const updatedDroppedButtons = droppedButtons.filter((button) => button !== data);
    setDroppedButtons(updatedDroppedButtons);

    // Find the cell containing the button and remove it
    const buttonCells = document.querySelectorAll(`td:contains(${data})`);
    buttonCells.forEach((cell) => {
      const buttonWrapper = cell.querySelector(".button-wrapper");
      cell.removeChild(buttonWrapper);

      // Get the updated cell content and update the table data state
      const updatedContent = cell.innerHTML;
      const rowIndex = cell.parentNode.rowIndex;
      const cellIndex = cell.cellIndex;
      const updatedTableData = [...tabledata];
      updatedTableData[rowIndex][cellIndex] = updatedContent;
      settabledata(updatedTableData);
    });
  };

  return (
    <div
      style={{ width: 100, height: 100, backgroundColor: "red" }}
      onDrop={handleDrop}
      onDragOver={(event) => event.preventDefault()}
    >
      drop here to delete
    </div>
  );
}

export default Deletebtn;
